        //             <MDBCol md="2">
        //             <i class="fas fa-trash" onClick={() => this.deleteWishList(data._id)}></i>
        //             </MDBCol>
        //             <MDBCol md="1"></MDBCol>
        //         </MDBRow>
        //     </div>
        // )

        return (
            <div>
               <MDBCol>
                    <MDBCard>
                        <MDBCardBody>

                            <MDBCardTitle>
                                <MDBRow>
                                    <MDBCol md="2">Product</MDBCol>
                                    <MDBCol md="2">Name</MDBCol>
                                    <MDBCol md="2">Price</MDBCol>
                                    <MDBCol md="2">Quantity</MDBCol>
                                    <MDBCol md="2">Total</MDBCol>
                                    <MDBCol md="2">Action</MDBCol>
                                </MDBRow>
                            </MDBCardTitle>
                            <MDBCardText>
                                {/* {displayData} */}
                            </MDBCardText>
                        </MDBCardBody>
                    </MDBCard>
                </MDBCol>
            </div>
        );
    }
}

export default Cart;
