import React from 'react';
import API from '../../../service/homeservice';
import { config } from '../../../config';
import "react-responsive-carousel/lib/styles/carousel.min.css";
import { Carousel } from 'react-responsive-carousel';

class Product extends React.Component {
    constructor(props) {
        console.log("props Home======", props);
        super(props);
        this.state = {
            bannerList: [],
            images: [],
            isFetching: true,
        }
    }

    componentDidMount() {
        console.log("msg axios calling")
        API.BannerList().
            then((findresponse) => {
                this.state.isFetching = false;
                console.log("BannerList response===", findresponse);
                this.setState({ bannerList: findresponse.data.data });
                console.log("BannerList response===", this.state.bannerList);
                this.state.bannerList.map(data => {
                    this.setState({
                        images: [...this.state.images, config.baseMediaUrl + data.imagePath + data.image]
                    })
                }
                )
            }).catch(
                { status: 500, message: 'Internal Server Error' }
            );
    }

    render() {
        const { isFetching } = this.state;
        let displayData;
        console.log("data=", this.state.bannerList);

        if (!isFetching && this.state.bannerList) displayData = this.state.images.map(data =>
            <Carousel >
                <div>
                    <img src={data} />
                </div>
            </Carousel>
        )
        return (
            <div>
                <div>
                    {displayData}
                </div>
                {/* <section className="banner_slider_section">
                    <div className="container-fluid">
                        <div className="banner_slider">
                            <div className="single_banner_slide">
                                <div className="slider_parts">
                                    {displayData}
                                    <a className="banner_view_all_btn" href="#">
                                        <span>View all Products</span>
                                    </a>
                                </div>
                            </div>
                        </div>
                    </div>
                </section> */}
            </div>
        );
    }
}

export default Product;
