import React from 'react';
import API from '../../service/homeservice';
import { MDBContainer, MDBRow, MDBCol, MDBBtn } from 'mdbreact';
import { BrowserRouter as Router, Route, Link } from "react-router-dom";

class Address extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            getProfileList: [],
            addressList: [],
            id: '',
            address_1: '',
            address_1Error: '',
            address_2: '',
            city: '',
            cityError: '',
            state: '',
            stateError: '',
            pincode: '',
            pincodeError: '',
            addressId:''
        }
        this.getAddress = this.getAddress.bind(this);
        this.addressDelete = this.addressDelete.bind(this);
    }

    componentDidMount() {
        API.getProfile().
            then((findresponse) => {
                console.log("getProfile response===", findresponse);
                this.setState({ getProfileList: findresponse.data.data })
                console.log("data==", this.state.getProfileList);
                this.setState({
                    id: this.state.getProfileList.id
                });
                this.getAddress(this.state.id);
            }).catch(
                { status: 500, message: 'Internal Server Error' }
            );
    }


    getAddress(id) {
        console.log("id===", id);
        API.getAddress(id).
            then((findresponse) => {
                console.log("getAddress response===", findresponse);
                this.setState({ addressList: findresponse.data.data });
                console.log("addressList[[[[[[===---", this.state.addressList);
                this.setState({addressId:this.state.addressList.addressId});
            }).catch(
                { status: 500, message: 'Internal Server Error' }
            );
    }

    addressDelete() {
        const addressId = this.state.addressId;
        console.log("addressid===",addressId);
        API.deleteAddress(deleteId).
        then((findresponse) => {
            console.log("deleteAddress response===", findresponse);
        }).catch(
            { status: 500, message: 'Internal Server Error' }
        );
    }

    render() {
        let displayData;
        if (this.state.addressList) displayData = this.state.addressList.map(data =>
            <div>
                <div className="grey-text">
                    Address
            </div>
                <div className="row">
                    <div className="col-md-8">
                        <p>{data.address1}</p>
                        <p>{data.address2}</p>
                        <p>{data.city}</p>
                        <p>{data.postcode}</p>
                        <p>{data.state}</p>
                    </div>
                    <div className="col-md-4">
                        <Link to={{ pathname: `/editaddress/${data.addressId}`, state: { name:data } }}><i class="fas fa-edit"></i></Link>
                        <i class="fas fa-trash" onClick={this.addressDelete}></i>
                    </div>
                </div>
                <br />
            </div>
        )
        return (
            <div>
                <MDBContainer>
                    <MDBRow>
                        <div>
                            {displayData}
                        </div>
                    </MDBRow>
                    <div className="text-center mt-4">
                        <Link to="/addnewaddress"><MDBBtn color="unique">
                            AddNewAddress
                        </MDBBtn></Link>
                    </div>
                </MDBContainer>
            </div>
        );
    }
}

export default Address;
