import React from 'react';
import API from '../../service/homeservice';
import { MDBContainer, MDBRow, MDBCol, MDBBtn } from 'mdbreact';
import { BrowserRouter as Router, Route, Link } from "react-router-dom";

class Profile extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            getProfileList: [],
            firstName: '',
            email: '',
            mobileNumber: ''
        }
        this.handleChangeName = this.handleChangeName.bind(this);
        this.editProfile = this.editProfile.bind(this);
    }

    componentDidMount() {
        API.getProfile().
            then((findresponse) => {
                console.log("getProfile response===", findresponse);
                this.setState({ getProfileList: findresponse.data.data })
                console.log("data==", this.state.getProfileList);
                this.setState({
                    firstName: this.state.getProfileList.firstName,
                    email: this.state.getProfileList.email,
                    mobileNumber: this.state.getProfileList.mobileNumber
                });
                console.log("email", this.state.email)
            }).catch(
                { status: 500, message: 'Internal Server Error' }
            );
    }

    handleChangeName(event) {
        const { name, value } = event.target;
        this.setState({
            [name]: value
        })
    }

    editProfile() {
        console.log("data:", this.state.email + this.state.firstName)
        const obj = {
            firstName: this.state.firstName,
            email: this.state.email,
            phoneNumber: this.state.mobileNumber
        }
        API.updateProfile(obj).
            then((findresponse) => {
                console.log("getProfile response===", findresponse);
            }).catch(
                { status: 500, message: 'Internal Server Error' }
            );
    }

    render() {
        return (
            <div>
                <MDBContainer>
                    <MDBRow>
                        <MDBCol md="3">
                            <form>
                                <label className="grey-text">
                                    Your name
                                </label>
                                <input
                                    type="text"
                                    name="firstName"
                                    value={this.state.firstName}
                                    onChange={this.handleChangeName}
                                    className="form-control"
                                />
                                <br />
                                <label className="grey-text">
                                    Your email
                                </label>
                                <input
                                    type="email"
                                    name="email"
                                    value={this.state.email}
                                    onChange={this.handleChangeName}
                                    className="form-control"
                                />
                                <br />
                                <label
                                    className="grey-text"
                                >
                                    Phone Number
                                </label>
                                <input
                                    type="text"
                                    name="mobileNumber"
                                    value={this.state.mobileNumber}
                                    onChange={this.handleChangeName}
                                    className="form-control"
                                />
                                <br />
                                <div className="text-center mt-4">
                                    <MDBBtn color="unique" onClick={this.editProfile} >
                                        Save
                                 </MDBBtn>
                                    <div>
                                        <span>
                                            <p><Link to="/updatepassword">update password</Link></p>
                                        </span>
                                    </div>
                                </div>
                            </form>
                        </MDBCol>

                        <MDBCol md="9">
                            <MDBCol md="4">
                                <form>
                                    <label className="grey-text">
                                        Address_1
                                </label>
                                    <input
                                        type="text"
                                        name="address_1"
                                        value={this.state.address_1}
                                        onChange={this.handleChangeName}
                                        className="form-control"
                                    />
                                    <br />
                                    <label className="grey-text">
                                        City
                                </label>
                                    <input
                                        type="text"
                                        name="city"
                                        value={this.state.city}
                                        onChange={this.handleChangeName}
                                        className="form-control"
                                    />
                                    <br />
                                    <label
                                        className="grey-text"
                                    >
                                        Pincode
                                </label>
                                    <input
                                        type="text"
                                        name="pincode"
                                        value={this.state.pincode}
                                        onChange={this.handleChangeName}
                                        className="form-control"
                                    />
                                    <br />

                                </form>
                            </MDBCol>
                            <MDBCol md="4">
                                <form>
                                    <label className="grey-text">
                                        Address_2
                                </label>
                                    <input
                                        type="text"
                                        name="address_2"
                                        value={this.state.address_2}
                                        onChange={this.handleChangeName}
                                        className="form-control"
                                    />
                                    <br />
                                    <label className="grey-text">
                                        State
                                </label>
                                    <input
                                        type="text"
                                        name="state"
                                        value={this.state.state}
                                        onChange={this.handleChangeName}
                                        className="form-control"
                                    />
                                    <br />
                                    <label
                                        className="grey-text"
                                    >
                                        Address-type
                                </label>
                                    <div class="custom-control custom-radio">
                                        <input type="radio" class="custom-control-input" id="defaultUnchecked" name="defaultExampleRadios" checked/>
                                        <label class="custom-control-label" for="defaultUnchecked">Home</label>
                                    </div>
                                    <div class="custom-control custom-radio">
                                        <input type="radio" class="custom-control-input" id="defaultChecked" name="defaultExampleRadios"/>
                                        <label class="custom-control-label" for="defaultChecked">Work</label>
                                    </div>
                                    <br />
                                </form>
                            </MDBCol>
                            <div className="text-center mt-4">
                                <MDBBtn color="unique" onClick={this.editProfile} >
                                    Save
                                 </MDBBtn>
                            </div>
                        </MDBCol>
                    </MDBRow>
                </MDBContainer>
            </div>
        );
    }
}

export default Profile;
