import React from 'react';
import { MDBContainer, MDBRow, MDBBtn } from 'mdbreact';
// import API from '../../service/signup.service';
import '../login/login.css'

class SignUp extends React.Component {
	/** constructor call */
	constructor(props) {
		console.log("props======", props);
		super(props);
		this.state = {
			name: '',
			nameError: '',
			password: '',
			passwordError: '',
			emailId: '',
            emailIdError: '',
            confirmPassword:'',
            confirmPasswordError:'',
            phoneNumber:'',
            phoneNumberError:''
		};
        this.Signup = this.Signup.bind(this);
		this.handleChangeEvent = this.handleChangeEvent.bind(this);
    }

    validate = () => {
		let nameError = "";
		let passwordError = "";
		let emailIdError = "";
		let confirmPasswordError = "";
		let phoneNumberError = "";

		if (!this.state.firstName) {
			firstNameError = "please enter first name";
		}

		if (!this.state.lastName) {
			lastNameError = "please enter last name";
		}

		if (!this.state.userName) {
			userNameError = "please enter user name";
		}

		const reg = /^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w{2,3})+$/;
		if (!reg.test(this.state.email)) {
			emailError = "invalid email";
		}
		const regex = /^(?=.*[0-9])(?=.*[!@#$%^&*])[a-zA-Z0-9!@#$%^&*]{6,16}$/;
        if (!regex.test(this.state.password)) {
            passwordError = "please enter old password";
        }

		if (firstNameError || lastNameError || userNameError || emailError || passwordError) {
			this.setState({ firstNameError, lastNameError, userNameError, emailError, passwordError });
			return false;
		}
		return true;
	};



	render() {
		return (
			<div>
				<MDBContainer>
					<MDBRow>
						<form>
							<p className="h4 text-center mb-4">Sign up</p>
							<label className="grey-text">
                            Name
            					</label>
							<input
								type="text"
								className="form-control"
								name="firstName"
								value={this.state.name}
								onChange={this.handleChangeEvent}
							/>
							<div style={{ fontSize: 12, color: "red" }}>
								{this.state.nameError}
							</div>
							<br />
							<label className="grey-text">
								 Email
           						 </label>
							<input
								type="email"
								name="email"
								className="form-control"
								value={this.state.emailId}
								onChange={this.handleChangeEvent}
							/>
							<div style={{ fontSize: 12, color: "red" }}>
								{this.state.emailIdError}
							</div>
							<br />
							<label
								className="grey-text"
							>
								Password
          						  </label>
							<input
								type="password"
								className="form-control"
								name="password"
								value={this.state.password}
								onChange={this.handleChangeEvent}
							/>
							<div style={{ fontSize: 12, color: "red" }}>
								{this.state.passwordError}
							</div>
                            <label
								className="grey-text"
							>
								Password
          						  </label>
							<input
								type="password"
								className="form-control"
								name="confirmPassword"
								value={this.state.confirmPassword}
								onChange={this.handleChangeEvent}
							/>
							<div style={{ fontSize: 12, color: "red" }}>
								{this.state.confirmPasswordError}
							</div>
                            <label
								className="grey-text"
							>
								PhoneNumber
          						  </label>
							<input
								type="text"
								className="form-control"
								name="phoneNumber"
								value={this.state.phoneNumber}
								onChange={this.handleChangeEvent}
							/>
							<div style={{ fontSize: 12, color: "red" }}>
								{this.state.phoneNumberError}
							</div>
							<div className="text-center mt-4">
								<MDBBtn color="unique" onClick={this.Signup} disabled={!this.state.name}>
									SignUp
								</MDBBtn>
							</div>
						</form>
					</MDBRow>
				</MDBContainer>
			</div>
		);
	}
}

export default SignUp;
