import React from 'react';
import API from '../../service/homeservice';
import { config } from '../../config';
import "react-responsive-carousel/lib/styles/carousel.min.css";
import { Carousel } from 'react-responsive-carousel';

class SingleProduct extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            productDetailList: [],
            images: [],
            isFetching: true
        }
    }

    componentDidMount() {
        console.log("query=", this.props.location.pathname.split('/')[2]);
        const query = this.props.location.pathname.split('/')[2];
        API.productDetail(query).
            then((findresponse) => {
                this.state.isFetching = false;
                console.log("productDetail response===", findresponse);
                this.setState({ productDetailList: findresponse.data.data });
                this.state.productDetailList.map(data => {
                    data.productImage ? (
                        data.productImage.map(list => {
                            list.containerName ? (this.setState({
                                images: [...this.state.images, config.baseMediaUrl + list.containerName + list.image]
                            })) : ('')
                        }
                        )
                    ) : (null)
                }
                )
            }).catch(
                { status: 500, message: 'Internal Server Error' }
            );
    }

    render() {
        let displayData;
        console.log("path images===", this.state.images);
        const { isFetching } = this.state;

        if (this.state.productDetailList) displayData = this.state.productDetailList.map(data =>
            <div className="single_product" data-aos="flip-left" data-aos-duration="1500">
                <div className="product_content">
                    <div className="product_image">
                        {(data.productImage) ?
                            (
                            data.productImage.map(list=>
                                <div>
                                {list.containerName ? (<img src={config.baseMediaUrl + list.containerName + list.image} className="img-fluid" alt="product image" />) : (<img src={config.baseMediaUrl + list.image} className="img-fluid" alt="product image" />)}
                            </div>
                                )
                         ) : ('')}

                        <div className="on_hover_btns">
                            <div className="d-flex flex-wrap align-content-center">
                                <div className="text-center p-2 m-auto">
                                    <Link to={`/singleproduct/${data._id}`}><i className="fa fa-eye" aria-hidden="true"></i></Link>
                                    <Link to="/cart"><i className="fa fa-shopping-cart" aria-hidden="true"></i></Link>
                                    <Link to="/wishlist"><i className="fa fa-heart" aria-hidden="true"></i></Link>
                                </div>
                            </div>
                        </div>
                    </div>
                    <div className="product_text">
                        <span className="product_type">{data.metaTagTitle}</span>
                        <p>{data.name}</p>
                        <ul>
                            <li className="fill"><i className="fa fa-star" aria-hidden="true"></i></li>
                            <li className="fill"><i className="fa fa-star" aria-hidden="true"></i></li>
                            <li className="fill"><i className="fa fa-star" aria-hidden="true"></i></li>
                            <li className="fill"><i className="fa fa-star" aria-hidden="true"></i></li>
                            <li><i className="fa fa-star" aria-hidden="true"></i></li>
                        </ul>
                        <p><i class="fas fa-rupee-sign"></i> <span className="procuct_price">{data.price}</span></p>
                    </div>
                </div>
            </div>
        )

        if (!isFetching && this.state.images) displayData = this.state.images.map(data =>
            <div>
                <Carousel>
                    <div>
                        <img src={data} />
                    </div>
                </Carousel>
            </div>
        )

        return (
            <div>
            <section className="product_section">
                <div className="container">
                    <div className="section_title">
                        <h3>New Products</h3>
                    </div>
                    <div className="product_slider">
                        {displayData}
                    </div>
                </div>
            </section>
        </div>
        );
    }
}

export default SingleProduct;
