import React from 'react';
import API from '../../../service/homeservice';
import { config } from '../../../config';
import "react-responsive-carousel/lib/styles/carousel.min.css";
import { Carousel } from 'react-responsive-carousel';

class Product extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            bannerList: [],
            images: [],
            isFetching: true,
        }
    }

    /** Intailly call */
    componentDidMount() {
        /** Get bannerlist */
        API.BannerList().
            then((findresponse) => {
                this.state.isFetching = false;
                this.setState({ bannerList: findresponse.data.data });
                this.state.bannerList.map(data => {
                    this.setState({
                        images: [...this.state.images, config.baseMediaUrl + data.imagePath + data.image]
                    })
                })
            }).catch(
                { status: 500, message: 'Internal Server Error' }
            );
    }

    render() {
        return (
            <div>
                <section class="banner_slider_section">
                    <div class="container-fluid">
                        <div class="banner_slider">
                            <div class="single_banner_slide">
                                <div class="slider_parts">
                                    <div class="box_one">
                                        <img src="images/slide_part_1.jpg" alt="slider box images" />
                                        <h2 class="cut_text">SALE</h2>
                                    </div>
                                    <div class="box_two">
                                        <div class="box_two_first">
                                            <img src="images/slide_part_3.jpg" alt="slider box images" />
                                        </div>
                                        <div class="box_two_second">
                                            <img src="images/slide_part_4.jpg" alt="slider box images" />
                                        </div>
                                    </div>
                                    <div class="box_three">
                                        <img src="images/slide_part_2.jpg" alt="slider box images" />
                                        <h2 class="cut_text">SALE</h2>
                                    </div>
                                    <div class="box_four">
                                        <div class="offer_content_text">
                                            <h2><small>UPTO</small>50%</h2>
                                            <a href="#">Terms and conditions apply</a>
                                        </div>
                                    </div>
                                    <a class="banner_view_all_btn" href="#">
                                        <span>View all Products</span>
                                    </a>
                                </div>
                            </div>
                        </div>
                    </div>
                </section>
                {/** Carousel */}
                {/* <Carousel>
                    {
                        this.state.images.map(data =>
                            <div>
                                <img src={data} />
                            </div>
                        )
                    }
                </Carousel> */}
            </div>
        );
    }
}

export default Product;
