import React from 'react';
import API from '../../service/homeservice';
import { MDBContainer, MDBRow, MDBCol, MDBBtn } from 'mdbreact';
import { BrowserRouter as Router, Route, Link } from "react-router-dom";

class Profile extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            getProfileList: [],
            addressList:[],
            id:'',
            firstName: '',
            email: '',
            mobileNumber: '',
            address_1: '',
            address_1Error: '',
            address_2: '',
            city: '',
            cityError: '',
            state: '',
            stateError: '',
            pincode: '',
            pincodeError: ''
        }
        this.handleChangeName = this.handleChangeName.bind(this);
        this.editProfile = this.editProfile.bind(this);
        this.addAddress = this.addAddress.bind(this);
        this.getAddress = this.getAddress.bind(this);
    }

    componentDidMount() {
        API.getProfile().
            then((findresponse) => {
                console.log("getProfile response===", findresponse);
                this.setState({ getProfileList: findresponse.data.data })
                console.log("data==", this.state.getProfileList);
                this.setState({
                    firstName: this.state.getProfileList.firstName,
                    email: this.state.getProfileList.email,
                    mobileNumber: this.state.getProfileList.mobileNumber,
                    id:this.state.getProfileList.id
                });
                this.getAddress(this.state.id);
            }).catch(
                { status: 500, message: 'Internal Server Error' }
            );
    }

    handleChangeName(event) {
        const { name, value } = event.target;
        this.setState({
            [name]: value
        })
    }

    editProfile() {
        const obj = {
            firstName: this.state.firstName,
            email: this.state.email,
            phoneNumber: this.state.mobileNumber
        }
        API.updateProfile(obj).
            then((findresponse) => {
                console.log("getProfile response===", findresponse);
            }).catch(
                { status: 500, message: 'Internal Server Error' }
            );
    }

    addAddress() {
        const obj = {
            customerId: this.state.getProfileList.id,
            address1: this.state.address_1,
            address2: this.state.address_2,
            city:this.state.city,
            state:this.state.state,
            postcode:this.state.postcode
        }

        API.addAddress(obj).
        then((findresponse) => {
            console.log("addAddress response===", findresponse);
        }).catch(
            { status: 500, message: 'Internal Server Error' }
        );
    }

    getAddress(id) {
        console.log("id===",id);
        API.getAddress(id).
        then((findresponse) => {
            console.log("getAddress response===", findresponse);
            this.setState({addressList:findresponse.data.data});
            console.log("addressList[[[[[[===---",this.state.addressList);
        }).catch(
            { status: 500, message: 'Internal Server Error' }
        );
    }

    render() {
        let displayData;
        if(this.state.addressList) displayData = this.state.addressList.map(data => 
            <div>
                  <p>{data.address1}</p>
                  <p>{data.address2}</p>
                  <p>{data.city}</p>
                  <p>{data.state}</p>
            </div>
            )
        return (
            <div>
                <MDBContainer>
                    <MDBRow>
                        <MDBCol md="3">
                            <form>
                                <label className="grey-text">
                                    Your name
                                </label>
                                <input
                                    type="text"
                                    name="firstName"
                                    value={this.state.firstName}
                                    onChange={this.handleChangeName}
                                    className="form-control"
                                />
                                <br />
                                <label className="grey-text">
                                    Your email
                                </label>
                                <input
                                    type="email"
                                    name="email"
                                    value={this.state.email}
                                    onChange={this.handleChangeName}
                                    className="form-control"
                                />
                                <br />
                                <label
                                    className="grey-text"
                                >
                                    Phone Number
                                </label>
                                <input
                                    type="text"
                                    name="mobileNumber"
                                    value={this.state.mobileNumber}
                                    onChange={this.handleChangeName}
                                    className="form-control"
                                />
                                <br />

                                <div className="text-center mt-4">
                                    <MDBBtn color="unique" onClick={this.editProfile} >
                                        Save
                                 </MDBBtn>
                                    <div>
                                        <span>
                                            <p><Link to="/updatepassword">update password</Link></p>
                                        </span>
                                    </div>
                                </div>
                            </form>
                        </MDBCol>

                        <MDBCol md="9">
                            <MDBRow>
                                <MDBCol md="6">
                                    <form>
                                        <label className="grey-text">
                                            Address_1
                                </label>
                                        <input
                                            type="text"
                                            name="address_1"
                                            value={this.state.address_1}
                                            onChange={this.handleChangeName}
                                            className="form-control"
                                        />
                                        <br />
                                        <label className="grey-text">
                                            City
                                </label>
                                        <input
                                            type="text"
                                            name="city"
                                            value={this.state.city}
                                            onChange={this.handleChangeName}
                                            className="form-control"
                                        />
                                        <br />
                                        <label
                                            className="grey-text"
                                        >
                                            Pincode
                                </label>
                                        <input
                                            type="text"
                                            name="pincode"
                                            value={this.state.pincode}
                                            onChange={this.handleChangeName}
                                            className="form-control"
                                        />
                                        <br />

                                    </form>
                                </MDBCol>

                                <MDBCol md="6">
                                    <form>
                                        <label className="grey-text">
                                            Address_2
                                </label>
                                        <input
                                            type="text"
                                            name="address_2"
                                            value={this.state.address_2}
                                            onChange={this.handleChangeName}
                                            className="form-control"
                                        />
                                        <br />
                                        <label className="grey-text">
                                            State
                                </label>
                                        <input
                                            type="text"
                                            name="state"
                                            value={this.state.state}
                                            onChange={this.handleChangeName}
                                            className="form-control"
                                        />
                                        <br />
                                    </form>
                                </MDBCol>
                            </MDBRow>
                            <div className="text-center mt-4">
                                <MDBBtn color="unique" onClick={this.addAddress} >
                                    Save
                                 </MDBBtn>
                            </div>
                        </MDBCol>
                    </MDBRow>
                    <MDBRow>
                    <div className="grey-text">
                        Address
                    </div>
                    <div>
                      {displayData}
                    </div>
                    </MDBRow>
                </MDBContainer>
            </div>
        );
    }
}

export default Profile;
