    render() {
        return (
            <div>
                <MDBContainer>
                    <MDBRow>
                        <MDBCol md="6">
                            <form>
                                <label className="grey-text">
                                    Your name
                                </label>
                                <input
                                    type="text"
                                    name="firstName"
                                    value={this.state.firstName}
                                    onChange={this.handleChange}
                                    className="form-control"
                                />
                                <br />
                                <label className="grey-text">
                                    Your email
                                </label>
                                <input
                                    type="email"
                                    name="email"
                                    value={this.state.email}
                                    onChange={this.handleChange}
                                    className="form-control"
                                />
                                <br />
                                <label
                                    className="grey-text"
                                >
                                    Phone Number
                                </label>
                                <input
                                    type="text"
                                    name="mobileNumber"
                                    value={this.state.mobileNumber}
                                    onChange={this.handleChange}
                                    className="form-control"
                                />
                                <br />
                                <div className="text-center mt-4">
                                    <MDBBtn color="unique">
                                        Save
                                 </MDBBtn>
                                </div>
                            </form>
                        </MDBCol>
                    </MDBRow>
                </MDBContainer>
            </div>
        );
    }
}

export default Profile;
