import React from 'react';
import { BrowserRouter as Router, Route, Link } from "react-router-dom";
import history from '../../../history';
import API from '../../../service/homeservice';
// import '../../../../public/css/style.css';
import './header.css';
import { EventEmitter } from '../../../event';

class Header extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            showing: true,
            categoryList: [],
            value: '',
            searchList: [],
            count: '',
            isLoaded: false
        }
        this.name = localStorage.getItem('name');
        this.logout = this.logout.bind(this);
        this.handleLoginKeyUp = this.keyUpHandler.bind(this);
        this.onClick = this.onClick.bind(this);
        EventEmitter.subscribe('length', (event) => {
            console.log("wishlist=", event);
            this.count = event;
            console.log("count=>", this.count);
            this.setState({ count: this.count })
        });
    }

    /** User Logout */
    logout() {
        localStorage.removeItem('token');
        localStorage.removeItem('name');
        localStorage.removeItem('productId');
        localStorage.removeItem('cartCount');
        localStorage.removeItem('wishlistLength');
        history.push('/home');
    }

    /** Intailly call */
    componentDidMount() {
        /** Get Categorylist */
        API.CategoryList().
            then((findresponse) => {
                this.setState({ categoryList: findresponse.data.data, isLoaded: true })
                console.log("data==", this.state.categoryList);
            }).catch(
                { status: 500, message: 'Internal Server Error' }
            );
    }

    /** 
  * @param {string} e
  * key up handler
  */
    keyUpHandler(e) {
        console.log("e", e.target.value);
        this.setState({ value: e.target.value })
        console.log("event====", this.state.value);
        /** SearchList */
        API.searchList(this.state.value).
            then((findresponse) => {
                window.location.href = '/searchproduct/' + this.state.value;
                console.log("searchList response===", findresponse);
                this.setState({ searchList: findresponse.data.data.productList })
                console.log("data==", this.state.searchList);
            }).catch(
                { status: 500, message: 'Internal Server Error' }
            );
    }

    onClick() {
        window.location.href = '/home';
    }

    render() {
        const { isLoaded } = this.state;
        if (!isLoaded) {
            return (
                <center>
                    <div className="loader"></div>
                </center>
            )
        } else if (isLoaded) {
            return (
                <div>
                    <header>
                        {/** Top Header */}
                        <div className="header_top">
                            <div className="container">
                                <div class="top_select float-left">
                                    <div class="custom_select language">
                                        <a href="javascript:void(0);">ENG</a>
                                        <div class="languages" style={{
                                            display: "none"
                                        }}>
                                            <ul>
                                                <li><a href="#">ENG</a></li>
                                                <li><a href="#">FRE</a></li>
                                            </ul>
                                        </div>
                                    </div>
                                    <div class="custom_select currency">
                                        <a href="javascript:void(0);">$ USD</a>
                                        <div class="currencies" style={{
                                            display: "none"
                                        }}>
                                            <ul>
                                                <li><a href="#">$ USD</a></li>
                                                <li><a href="#">â‚¬ EUR</a></li>
                                            </ul>
                                        </div>
                                    </div>
                                </div>
                                <div class="contact_number float-left">
