import React from 'react';
import API from '../../../service/homeservice';

class Product extends React.Component {
    constructor(props) {
        console.log("props Home======", props);
        super(props);
        this.state = {
            bannerList:[]
        }
    }

    componentDidMount(){
        console.log("msg axios calling")
        API.BannerList().
        then((findresponse) => {
          console.log("BannerList response===", findresponse);
          this.setState({bannerList:findresponse.data.data});
          console.log("BannerList response===", this.state.bannerList);
        }).catch(
          { status: 500, message: 'Internal Server Error' }
        );
    }

    render() {
        let displayData;
        console.log("data=",this.state.bannerList);

        if(this.state.bannerList) displayData = this.state.bannerList.map(data => 
            <section className="banner_slider_section">
            <div className="container-fluid">
                <div className="banner_slider">
                    <div className="single_banner_slide">
                        <div className="slider_parts">
                            <div className="box_one">
                                <img src={require('../images/slide_part_1.jpg')} alt="slider box images" />
                                <h2 className="cut_text">SALE</h2>
                            </div>
                            <div className="box_two">
                                <div className="box_two_first">
                                    <img src={require('../images/slide_part_3.jpg')} alt="slider box images" />
                                </div>
                                <div className="box_two_second">
                                    <img src={require('../images/slide_part_4.jpg')} alt="slider box images" />
                                </div>
                            </div>
                            <div className="box_three">
                                <img src={require('../images/slide_part_2.jpg')} alt="slider box images" />
                                <h2 className="cut_text">SALE</h2>
                            </div>
                            <div className="box_four">
                                <div className="offer_content_text">
                                    <h2><small>UPTO</small>50%</h2>
                                    <a href="#">Terms and conditions apply</a>
                                </div>
                            </div>
                            <a className="banner_view_all_btn" href="#">
                                <span>View all Products</span>
                            </a>
                        </div>
                    </div>
                </div>
            </div>
        </section>
            )
        return (
            <div>
               {displayData}
            </div>
        );
    }
}

export default Product;
