import React from 'react';
import API from '../../service/homeservice';
import { config } from '../../config';
import "react-responsive-carousel/lib/styles/carousel.min.css";
import { Carousel } from 'react-responsive-carousel';
import { MDBContainer, MDBRow, MDBCol, MDBCard, MDBCardBody, MDBCardImage, MDBCardTitle, MDBCardText } from "mdbreact";
import './singleproduct.css';
import renderHTML from 'react-render-html';
import { BrowserRouter as Router, Route, Link } from "react-router-dom";
import Header from '../../Component/home/header/header';
import Footer from '../../Component/home/footer/footer';
import Swal from 'sweetalert2';
const _ = require('lodash');
let finalArr = [];
let priceArr = [];
let total = 0;

class SingleProduct extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            cartItem: '',
            demo: '',
            productDetailList: [],
            relatedProductList: [],
            images: [],
            isFetching: true,
            productId: ''
        }
        this.relatedProduct = this.relatedProduct.bind(this);
    }

    /** Intially call */
    componentDidMount() {
        const query = this.props.location.pathname.split('/')[2];
        /** Product details */
        API.productDetail(query).
            then((findresponse) => {
                this.state.isFetching = false;
                this.setState({ productDetailList: findresponse.data.data });
