import React from 'react';
import API from '../../service/homeservice';
import { MDBContainer, MDBRow, MDBCol, MDBBtn } from 'mdbreact';
import { BrowserRouter as Router, Route, Link } from "react-router-dom";

class Profile extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            getProfileList: [],
            name: '',
            email: '',
            phone: ''
        }
        this.handleChange = this.handleChange.bind(this);
    }

    componentDidMount() {
        API.getProfile().
            then((findresponse) => {
                console.log("getProfile response===", findresponse);
                this.setState({ getProfileList: findresponse.data.data })
                console.log("data==", this.state.getProfileList);
                this.setState({
                    name: this.state.getProfileList.firstName,
                    email: this.state.getProfileList.email,
                    phone: this.state.getProfileList.mobileNumber
                });

            }).catch(
                { status: 500, message: 'Internal Server Error' }
            );
    }

    handleChange(event) {
        console.log("event{}+====",event.target.value);
        // this.setState({ value: event.target.value });
    }

    editProfile() {
        const obj = {
            firstName: this.state.name,
            email: this.state.email,
            mobileNumber: this.state.phone
        }
        API.updateProfile(obj).
            then((findresponse) => {
                console.log("getProfile response===", findresponse);
                this.setState({ getProfileList: findresponse.data.data })
                console.log("data==", this.state.getProfileList);
                this.setState({
                    name: this.state.getProfileList.firstName,
                    email: this.state.getProfileList.email,
                    phone: this.state.getProfileList.mobileNumber
                });

            }).catch(
                { status: 500, message: 'Internal Server Error' }
            );
    }

    render() {
        return (
            <div>
                <MDBContainer>
                    <MDBRow>
                        <MDBCol md="6">
                            <form>
                                <label className="grey-text">
                                    Your name
                                </label>
                                <input
                                    type="text"
                                    value={this.state.name}
                                    onChange={this.handleChange}
                                    className="form-control"
                                />
                                <br />
                                <label className="grey-text">
                                    Your email
                                </label>
                                <input
                                    type="email"
                                    value={this.state.email}
                                    onChange={this.handleChange}
                                    className="form-control"
                                />
                                <br />
                                <label
                                    className="grey-text"
                                >
                                    Phone Number
                                </label>
                                <input
                                    type="text"
                                    value={this.state.phone}
                                    onChange={this.handleChange}
                                    className="form-control"
                                />
                                <br />
                                <div className="text-center mt-4">
                                    <MDBBtn color="unique" onClick={() => this.editProfile}>
                                        Save
                                 </MDBBtn>
                                </div>
                            </form>
                        </MDBCol>
                    </MDBRow>
                </MDBContainer>
            </div>
        );
    }
}

export default Profile;
