import React from 'react';
import API from '../../service/homeservice';
import { config } from '../../config';
import { MDBBtn, MDBCard, MDBCardBody, MDBRow, MDBCardTitle, MDBCardText, MDBCol } from 'mdbreact';
const _ = require('lodash');

class Cart extends React.Component {
    constructor(props) {
        console.log("props Home======", props);
        super(props);
        this.state = {
            cartItem:[]
        }

    }

    componentDidMount() {
        const value = localStorage.getItem('productId');
        const val = _.uniq(value.split(',')); //dplicate value remove
        console.log('value of token-=-=', val);
        const filter = _.filter(val, _.size); // null value remove
        console.log('value of filter-=-=', filter);
        filter.map((id) => {
            console.log('id-=-=', id);
            API.productDetail(id)
              .then((response) => {
                console.log('respone from cart-=-=', response.data.data);
                response.data.data[0]['qty'] = 1;
                this.setState({
                  cartItem: [...this.state.cartItem, ...response.data.data]
                })
                console.log('cartItem-=-=', this.state.cartItem);
              })
              .catch((err) => {
                console.log('err from cart-=-=', err);
              })
          })
    }

    render() {

        let displayData;

        if (this.state.cartItem) displayData = this.state.cartItem.map(data =>
            <div>
                <MDBRow>
                    <MDBCol md="1"></MDBCol>
                    <MDBCol md="2">
                        <div>
                            {(data.productImage) ?
                                (<div>
                                    {data.productImage.containerName ? (<img src={config.baseMediaUrl + data.productImage.containerName + data.productImage.image} className="img-fluid" alt="product image" />) : (<img src={config.baseMediaUrl + data.productImage.image} className="img-fluid" alt="product image" />)}
                                </div>) : ('')}
                        </div>
                    </MDBCol>
                    <MDBCol md="2">{data.product.name}</MDBCol>
                    <MDBCol md="2">{data.product.price}</MDBCol>
                    <MDBCol md="2">{data.product.dateAvailable}</MDBCol>
                    <MDBCol md="2">
                    <i class="fas fa-trash" onClick={() => this.deleteWishList(data._id)}></i>
                    </MDBCol>
                    <MDBCol md="1"></MDBCol>
                </MDBRow>
            </div>
        )

        return (
            <div>
               <MDBCol>
                    <MDBCard>
                        <MDBCardBody>

                            <MDBCardTitle>
                                <MDBRow>
                                    <MDBCol md="2">Product</MDBCol>
                                    <MDBCol md="2">Name</MDBCol>
                                    <MDBCol md="2">Price</MDBCol>
                                    <MDBCol md="2">Quantity</MDBCol>
                                    <MDBCol md="2">Total</MDBCol>
                                    <MDBCol md="2">Action</MDBCol>
                                </MDBRow>
                            </MDBCardTitle>
                            <MDBCardText>
                                {displayData}
                            </MDBCardText>
                        </MDBCardBody>
                    </MDBCard>
                </MDBCol>
            </div>
        );
    }
}

export default Cart;
